	$form_state['page_num'] = 1;
	$form_state['rebuild'] = TRUE;
}

function ajax_event_registration_form($form, &$form_state)
{
	$form['city'] = array(
		'#title' => t('Choose a city'),
		'#type' => 'select',
		'#options' => array(
			'World' => t('World'),
			'Chicago' => t('Chicago'),
			'New York' => t('New York'),
			'Los Angeles' => t('Los Angeles'),
		),
		'#ajax' => array(
			'callback' => 'ajax_event_registration_callback',
			'wrapper' => 'ajax_event_registration_wrapper',
		),
	);
	
	$form['ajax_markup'] = array(
		'#type' => 'markup',
		'#prefix' => '<div id="ajax_event_registration_wrapper">',
		'#suffix' => '</div>',
	);
	
	if( (isset($form_state['values']['city'])) && ($form_state['values']['city'] != '') ) 
	{
		$form['ajax_markup']['#markup'] = t('You choose '. $form_state['values']['city']);
	}

	return $form;
}

function ajax_event_registration_callback($form, $form_state)
{
	return $form['ajax_markup'];
}


